mapper.select:
SELECT * FROM User
ORDER BY userId
LIMIT #{offset}, #{size}

mapper.update:
UPDATE User
SET OverDueDate = 0
WHERE userId = #{userId}

UPDATE User
SET rentalAvailable = CAST(rentalAvailable AS SIGNED) - 1
WHERE userId = #{userId};

UPDATE User
SET rentalAvailable = CAST(rentalAvailable AS SIGNED) + 1
WHERE userId = #{userId};
